mapper.select:
SELECT COUNT(*)
FROM chat_room
WHERE room_id = #{room_id} AND user_id = #{user_id}

SELECT COUNT(*)
FROM chat_room
WHERE room_id = #{room_id}

SELECT room_id
FROM chat_room
WHERE user_id = #{user_id}

mapper.update:
UPDATE chat_room
SET plan_id = #{plan_id}
WHERE user_id = #{user_id} and
room_id = #{room_id}
